import React, { useEffect, useState } from 'react';
import { HashRouter as Router, Route, Routes } from 'react-router-dom';
import LandingPage from './pages/landingPage/LandingPage';
import CJSLanding from './pages/gameLanding/cjsLanding/CJSLanding';
import CJSInstruction from './pages/gameInstruction/cjsInstruction/CJSInstruction';
import CJSGame from './pages/game/cjsGame/CJSGame';
import LoadingSpinner from './components/loadingSpinner/LoadingSpinner';
import ParticipantForm from './pages/participantForm/participantForm';
import { getDataFromLocalStorage } from './uitls/offline';
import useDisableZoomAndResize from './hooks/useDisableZoomAndResize';

function App() {
  const [userId, setUserId] = useState<number | null>(null);

  // Use the custom hook
  useDisableZoomAndResize();

  // // Retrieve userId from localStorage
  // let id = getDataFromLocalStorage('userId');
  // if (id !== null) {
  //   // setUserId(id);
  // } else {
  //   if (window.location.href === "https://cccnlab.co/vss-visual-search/") {
  //     // Do nothing, stay on the same page
  //   } else {
  //     window.location.replace("https://cccnlab.co/vss-visual-search/");
  //   }
  // }

  useEffect(() => {
    const id = getDataFromLocalStorage('userId');
    if (id !== null) {
      setUserId(id); // ตรวจสอบให้แน่ใจว่า id ไม่ทำให้เกิดการเรนเดอร์ซ้ำ
    }
  }, []); // ใช้ dependency array ว่างเพื่อให้ useEffect ทำงานครั้งเดียว

  return (
    <Router>
      <Routes>
        <Route path="/" element={<ParticipantForm setUserId={setUserId} />} />
        <Route path="/landing" element={<LandingPage />} />
        <Route path="/conjunction-search" element={<CJSLanding />} />
        <Route path="/conjunction-search/instruction" element={<CJSInstruction />} />
        <Route path="/conjunction-search/trial" element={<CJSGame userId={userId} />} />
      </Routes>
      <LoadingSpinner />
    </Router>
  );
}

export default App;
