import React, { useState, useEffect } from 'react';
import './CJSButton.scss';

function CJSButton(props) {
  // สถานะในการแสดง/ซ่อนปุ่ม
  const [isButtonVisible, setIsButtonVisible] = useState(true);

  useEffect(() => {
    // ฟังก์ชันตรวจจับการกดแป้นพิมพ์
    function handleKeyDown(event) {
      if (event.code === 'ShiftLeft') {
        // กด Shift ซ้าย -> ทำงานเหมือนคลิกปุ่ม "มี"
        props.checkResp(1);  // เมื่อกด Shift ซ้าย
        setIsButtonVisible(false);  // ซ่อนปุ่ม
      } else if (event.code === 'ShiftRight') {
        // กด Shift ขวา -> ทำงานเหมือนคลิกปุ่ม "ไม่มี"
        props.checkResp(0);  // เมื่อกด Shift ขวา
        setIsButtonVisible(false);  // ซ่อนปุ่ม
      }
    }

    // เพิ่ม event listener สำหรับการกดแป้นพิมพ์
    document.addEventListener('keydown', handleKeyDown);

    // ทำความสะอาดเมื่อคอมโพเนนต์ถูกทำลาย
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [props.checkResp]);

  return (
    <div>
      {props.searchTarget && isButtonVisible ? (
        <div className="btnContainer">
          {/* ปุ่ม "ไม่มี" */}
          <button
            id="no-btn"
            disabled={props.disabledButton}
            className="btn no-btn circle lg"
            onMouseDown={() => {
              props.checkResp(0);
              setIsButtonVisible(false); // ซ่อนปุ่มหลังคลิก
            }}
          >
            ไม่มี
          </button>

          {/* ปุ่ม "มี" */}
          <button
            id="yes-btn"
            disabled={props.disabledButton}
            className="btn yes-btn circle lg"
            onMouseDown={() => {
              props.checkResp(1);
              setIsButtonVisible(false); // ซ่อนปุ่มหลังคลิก
            }}
          >
            มี
          </button>
        </div>
      ) : null}
    </div>
  );
}

export default CJSButton;
