import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { saveDataToLocalStorage } from '../../uitls/offline';

export default function ParticipantForm(props: {
    setUserId: (id: number) => void;
}) {
    const navigate = useNavigate();
    const [promptUserId, setPromptUserId] = useState<string>("");

    return (
        <div className="h-screen flex items-center justify-center">
            <div className="flex flex-col w-full max-w-md mx-auto px-6 py-12"> {/* ปรับ max-w-md */}
                <h2 className="text-center text-xl font-bold leading-9 tracking-tight text-gray-900">
                    กรุณากรอกข้อมูล
                </h2>

                <form className="mt-10 space-y-6">
                    <div>
                        <label htmlFor="userId" className="block text-sm font-medium leading-6 text-gray-900 text-center">
                            หมายเลขผู้เข้าร่วมงานวิจัย (Subject Number)
                        </label>
                        <div className="mt-2">
                            <input
                                onKeyDown={(e) => {
                                    if (!/[0-9]/.test(e.key) && e.key !== 'Backspace' && e.key !== 'Tab') {
                                        e.preventDefault();
                                    }
                                }}
                                onChange={(e) => {
                                    const value = e.target.value;
                                    if (/^\d*$/.test(value) && value.length <= 3) {
                                        setPromptUserId(value);
                                    }
                                }}
                                id="userId"
                                name="userId"
                                type="tel"
                                autoComplete="userId"
                                required
                                className="block w-full px-4 py-1.5 rounded-md border-0 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6 max-w-md mx-auto" 
                                placeholder=": ยกตัวอย่างเช่น 001"
                                maxLength={3}
                            />
                        </div>
                    </div>

                    <div>
                        <button
                            onClick={(e) => {
                                e.preventDefault();
                                const numValue = Number(promptUserId);
                                if (promptUserId === "") {
                                    alert("กรุณากรอกข้อมูลให้ครบถ้วนและถูกต้อง");
                                    setPromptUserId("");
                                } else if (promptUserId.length < 3) {
                                    alert("กรุณากรอกหมายเลขผู้เข้าร่วมงานวิจัยให้ครบ 3 หลัก");
                                    setPromptUserId("");
                                } else {
                                    if ((numValue < 1 || numValue > 16) && numValue !== 428) {
                                        alert("กรุณากรอกหมายเลขในช่วง 001 ถึง 016 หรือหมายเลข 428");
                                        setPromptUserId("");
                                    } else {
                                        props.setUserId(numValue);
                                        navigate("/landing");
                                        saveDataToLocalStorage('userId', numValue.toString().padStart(3, '0'));
                                    }
                                }
                            }}
                            className="flex w-full justify-center px-3 py-1.5 rounded-md bg-indigo-600 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600 max-w-md mx-auto" 
                        >
                            เข้าร่วมการทดสอบ
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}
