import React, { useEffect } from 'react';

let canvasWidth = 600;
let canvasHeight = 600;

function CJSWindow(props) {
  return (
    <div className="flex justify-center items-center h-full w-full relative">
      {/* ข้อความด้านซ้าย */}
      <div className="absolute left-0 top-1/2 transform -translate-y-1/2 text-center">
        <p className="text-gray-700 font-bold">มี<br /><span className="text-sm">กด Z</span></p>
      </div>

      {/* กล่อง canvas */}
      <div className="mx-auto flex flex-col items-center space-y-4">
        {props.searchTarget ? (
          <div className="searchInstruction text-center">
            มี
            <b
              className={
                'search-text ' +
                props.searchTargetList[props.searchTarget.shape][
                  props.searchTarget.col
                ].color
              }
            >
              {' '}
              {props.searchTargetList[props.searchTarget.shape][
                props.searchTarget.col
              ].description}
            </b>
            <span
              className={
                'search-img ' +
                props.searchTargetList[props.searchTarget.shape][
                  props.searchTarget.col
                ].shape +
                ' ' +
                props.searchTargetList[props.searchTarget.shape][
                  props.searchTarget.col
                ].color
              }
            ></span>
            หรือไม่?
          </div>
        ) : null}
        <canvas
          id="myCanvas"
          width={props.canvasWidth}
          height={props.canvasHeight}
          className="border border-gray-300 shadow-lg"
        ></canvas>
      </div>

      {/* ข้อความด้านขวา */}
      <div className="absolute right-0 top-1/2 transform -translate-y-1/2 text-center">
        <p className="text-gray-700 font-bold">ไม่มี<br /><span className="text-sm">กด X</span></p>
      </div>
    </div>
  );
}

export default CJSWindow;
