import React, { useEffect } from 'react'

let canvasWidth = 600;
let canvasHeight = 600;

function CJSWindow(props) {
  return (
    <div className="flex justify-center items-center h-full w-full relative">
      {/* ข้อความด้านซ้าย */}
      <div className="absolute top-1/2 left-[10%] transform -translate-y-1/2 -translate-x-1/2 text-center">
        <p className="text-gray-700 font-bold">
          มี<br />
          <span className="text-sm">กด Z</span>
        </p>
      </div>

      {/* ข้อความ searchInstruction อยู่เหนือกล่อง canvas */}
      <div className="absolute top-[1%] left-1/2 transform -translate-x-1/2 text-center">
          {props.searchTarget ? (
      <div className="searchInstruction text-center">
        มี
        <b></b>
        {/* แสดงรูปทรง */}
        {props.searchTargetList[props.searchTarget.shape][props.searchTarget.col].shape === 'circle' ? (
          <span
            className="inline-block w-6 h-6 rounded-full"
            style={{
              backgroundColor: props.searchTargetList[props.searchTarget.shape][props.searchTarget.col].color,
            }}
          ></span>
        ) : (
          <span
            className="inline-block w-6 h-6"
            style={{
              backgroundColor: props.searchTargetList[props.searchTarget.shape][props.searchTarget.col].color,
            }}
          ></span>
        )}
        <b
          className={
            'search-text ' +
            props.searchTargetList[props.searchTarget.shape][props.searchTarget.col].color
          }
        >
          {' '}
          {props.searchTargetList[props.searchTarget.shape][props.searchTarget.col].description}
        </b>
            หรือไม่?
          </div>
        ) : (
          'ไม่มี'
        )}
      </div>

      {/* กล่อง canvas */}
      <div className="relative flex justify-center items-center">
        <canvas
          id="myCanvas"
          width={props.canvasWidth}
          height={props.canvasHeight}
          className="border border-gray-300 shadow-lg"
        ></canvas>
      </div>

      {/* ข้อความด้านขวา */}
      <div className="absolute top-1/2 right-[10%] transform -translate-y-1/2 translate-x-1/2 text-center">
        <p className="text-gray-700 font-bold">
          ไม่มี<br />
          <span className="text-sm">กด X</span>
        </p>
      </div>
    </div>
  )
}

export default CJSWindow
