import React, { useEffect } from 'react';

const canvasWidth = 600;
const canvasHeight = 600;

function CJSWindow({ searchTarget, searchTargetList }) {
  useEffect(() => {
    const canvas = document.getElementById('myCanvas');
    if (canvas) {
      const ctx = canvas.getContext('2d');
      // ตัวอย่าง: ตั้งค่า background สีขาว
      ctx.fillStyle = '#ffffff';
      ctx.fillRect(0, 0, canvas.width, canvas.height);
    }
  }, []);

  return (
    <div className="flex justify-center items-center h-screen w-full p-5 bg-slate-50">
      <div className="flex flex-col items-center space-y-4 max-w-lg w-full">
        {searchTarget && (
          <div className="searchInstruction text-center">
            มี
            <b
              className={`search-text ${
                searchTargetList[searchTarget.shape][searchTarget.col].color
              }`}
            >
              {' '}
              {
                searchTargetList[searchTarget.shape][searchTarget.col]
                  .description
              }
            </b>
            <span
              className={`search-img ${
                searchTargetList[searchTarget.shape][searchTarget.col].shape
              } ${
                searchTargetList[searchTarget.shape][searchTarget.col].color
              }`}
            ></span>
            หรือไม่?
          </div>
        )}
        <canvas
          id="myCanvas"
          width={canvasWidth}
          height={canvasHeight}
          className="border border-gray-300 shadow-lg"
        ></canvas>
      </div>
    </div>
  );
}

export default CJSWindow;
