import { useNavigate } from 'react-router-dom';
import { useState } from 'react'
import { saveDataToLocalStorage } from '../../uitls/offline';

// function inputIsAllNumeric(number: string): boolean {
//     const numericRegex = /^[0-9]+$/;
//     return numericRegex.test(number);
// }

// function lengthIsinRange(text: string): boolean {
//     return text.length === 3;
// }

export default function ParticipantForm(props:{
  setUserId: (id: string) => void
}) {
  const navigate = useNavigate();
  const [promptUserId, setPromptUserId] = useState<string>('');

  return (
    <div className="h-screen">
      <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
        <div className="sm:mx-auto sm:w-full sm:max-w-sm ">
          <h2 className="mt-10 text-center text-xl font-bold leading-9 tracking-tight text-gray-900">
            กรุณากรอกข้อมูล
          </h2>
        </div>

        <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
          <form className="space-y-6" >
            <div>
              <label htmlFor="userId" className="block text-sm font-medium leading-6 text-gray-900 text-center">
                หมายเลขผู้เข้าร่วมงานวิจัย (Subject Number)
              </label>
              <div className="mt-2">
                <input 
                  onChange={(e) => {
                    const value = e.target.value;
                    if (!isNaN(Number(value)) && value.length === 3) {
                      setPromptUserId(value); // ไม่ต้องแปลงเป็น number ถ้าจะใช้ string
                    } else {
                      setPromptUserId(''); // ตั้งเป็นค่าว่างถ้าไม่ตรงเงื่อนไข
                    }
                  }}
                  id="userId"
                  name="userId"
                  type="tel"
                  autoComplete="userId"
                  required
                  className="block w-full px-4 rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                  placeholder=": ยกตัวอย่างเช่น 001" 
                  pattern="^\d{3}(-\d{2})?$"
                />
              </div>
            </div>

            <div>
              <button onClick={(e) => {
                e.preventDefault();
                if (promptUserId === '') {
                  alert("กรุณากรอกข้อมูลให้ครบถ้วนและถูกต้อง");
                } else {
                  props.setUserId(promptUserId); // ส่งเป็น string
                  navigate("/landing");
                  saveDataToLocalStorage('userId', promptUserId);
                }
              }}
                className="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600">
                เข้าร่วมการทดสอบ
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}
