import React, { useEffect, useState } from 'react';

function LoadingSpinner() {
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        setIsLoading(true);
        setTimeout(() => {
            setIsLoading(false);
        }, 50000);
    }, [])

  return (
    <div id="loading">
        {isLoading ?
            <div className="loading-panel fixed inset-0 z-50 bg-[rgba(73,73,73,0.4)] flex items-center justify-center">
                <div 
                    className="loader rounded-full bg-gradient-to-r from-cyan-500 to-blue-500 animate-spin"
                    style={{
                        position: 'fixed',
                        right: '2vw',  // ระยะห่างจากขอบขวา
                        bottom: '2vw', // ใช้ค่าเดียวกันกับ right
                        width: '5vw',  // ขนาดตาม viewport
                        height: '5vw', // ขนาดตาม viewport
                        background: 'linear-gradient(140deg, #24C6DC, #514A9D)',
                        mask: 'radial-gradient(farthest-side,#0000 calc(100% - 1.5vw),#fff 0)',
                        WebkitMask: 'radial-gradient(farthest-side,#0000 calc(100% - 1.5vw),#fff 0)',
                    }} 
                />
            </div>
        : null }
    </div>
  )
}

export default LoadingSpinner;
