import React, { useEffect, useState } from 'react';

function CJSWindow(props) {
  const [canvasWidth, setCanvasWidth] = useState(600);
  const [canvasHeight, setCanvasHeight] = useState(600);

  useEffect(() => {
    const updateCanvasSize = () => {
      // กำหนดขนาดของ canvas ไม่เกิน 90% ของขนาดหน้าจอ
      setCanvasWidth(window.innerWidth * 0.9);
      setCanvasHeight(window.innerHeight * 0.9);
    };

    // เรียกใช้ฟังก์ชันเพื่อกำหนดขนาดเริ่มต้น
    updateCanvasSize();

    // ฟังการเปลี่ยนขนาดหน้าจอ
    window.addEventListener('resize', updateCanvasSize);

    // ลบ event listener เมื่อ component ถูกทำลาย
    return () => {
      window.removeEventListener('resize', updateCanvasSize);
    };
  }, []);

  return (
    <div className="flex justify-center items-center h-full w-full">
      <div className="mx-auto flex flex-col items-center space-y-4">
        {props.searchTarget ? (
          <div className="searchInstruction text-center">
            มี
            <b
              className={
                'search-text ' +
                props.searchTargetList[props.searchTarget.shape][
                  props.searchTarget.col
                ].color
              }
            >
              {' '}
              {props.searchTargetList[props.searchTarget.shape][
                props.searchTarget.col
              ].description}
            </b>
            <span
              className={
                'search-img ' +
                props.searchTargetList[props.searchTarget.shape][
                  props.searchTarget.col
                ].shape +
                ' ' +
                props.searchTargetList[props.searchTarget.shape][
                  props.searchTarget.col
                ].color
              }
            ></span>
            หรือไม่?
          </div>
        ) : null}
        <canvas
          id="myCanvas"
          width={canvasWidth}
          height={canvasHeight}
          className="border border-gray-300 shadow-lg"
        ></canvas>
      </div>
    </div>
  );
}

export default CJSWindow;
