import React, { useEffect, useState } from 'react';

function LoadingSpinner() {
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        setIsLoading(true);
        setTimeout(() => {
            setIsLoading(false);
        }, 50000);
    }, []);

    // การคำนวณขนาดวงกลม
    const circleSize = `${(window.innerWidth + window.innerHeight) / 10.28}px`;
    const controlSize = `${(window.innerWidth + window.innerHeight) / 77.125}px`;

    return (
        <div id="loading" className="flex items-center justify-center h-screen">
            {isLoading ? (
                <div className="fixed inset-0 flex items-center justify-center bg-gray-700 bg-opacity-40">
                    <div
                        className="absolute"
                        style={{
                            left: `calc(50% - ${parseFloat(circleSize) / 2}px)`,
                            top: `calc(50% - ${parseFloat(circleSize) / 2}px)`,
                            width: circleSize,
                            height: circleSize,
                            background: 'linear-gradient(140deg, #24C6DC, #514A9D)',
                            borderRadius: '50%',
                            animation: 'spin 1.2s ease infinite',
                            mask: `radial-gradient(farthest-side, #0000 calc(100% - ${controlSize}), #fff 0)`,
                            WebkitMask: `radial-gradient(farthest-side, #0000 calc(100% - ${controlSize}), #fff 0)`,
                        }}
                    />
                </div>
            ) : null}
        </div>
    );
}

export default LoadingSpinner;
